{
  "process_name": "Capitec Client Offboarding Process",
  "actors": [
    "Capitec Bank",
    "Client"
  ],
  "steps": [
    {
      "id": "S1",
      "type": "start",
      "actor": "Capitec Bank",
      "description": "Offboarding process initiated.",
      "next": [
        "A1"
      ]
    },
    {
      "id": "A1",
      "type": "action",
      "actor": "Capitec Bank",
      "description": "Identify offboarding triggers (Client-initiated, Bank-initiated, Credit-driven).",
      "next": [
        "A2"
      ]
    },
    {
      "id": "A2",
      "type": "action",
      "actor": "Capitec Bank",
      "description": "Segment client (product mix, credit status, legal status, risk flags, portfolio) to determine legal allowance.",
      "next": [
        "D1"
      ]
    },
    {
      "id": "D1",
      "type": "decision",
      "actor": "Capitec Bank",
      "description": "Does the client have active credit facilities?",
      "next": [
        "A3",
        "A4"
      ]
    },
    {
      "id": "A3",
      "type": "action",
      "actor": "Capitec Bank",
      "description": "Apply Managed Restriction Model (reduce transactional capability, credit remains active, client no longer commercially active).",
      "next": [
        "D2"
      ]
    },
    {
      "id": "D2",
      "type": "decision",
      "actor": "Capitec Bank",
      "description": "Has the credit been fully repaid or legally resolved (e.g., written off, judgment settled, insolvency finalised)?",
      "next": [
        "A6"
      ]
    },
    {
      "id": "A6",
      "type": "action",
      "actor": "Capitec Bank",
      "description": "Close credit agreement.",
      "next": [
        "A7"
      ]
    },
    {
      "id": "A7",
      "type": "action",
      "actor": "Capitec Bank",
      "description": "Resume normal offboarding steps (product closures, data management).",
      "next": [
        "E2"
      ]
    },
    {
      "id": "E2",
      "type": "end",
      "actor": "Capitec Bank",
      "description": "Relationship terminated and client data retained per statutory timelines (post-credit resolution).",
      "next": []
    },
    {
      "id": "A4",
      "type": "action",
      "actor": "Capitec Bank",
      "description": "Settle account balances (zeroed) and clear pending items (debits, stop orders, card transactions).",
      "next": [
        "A5"
      ]
    },
    {
      "id": "A5",
      "type": "action",
      "actor": "Capitec Bank",
      "description": "Close all associated products (cards, savings pockets, access channels).",
      "next": [
        "E1"
      ]
    },
    {
      "id": "E1",
      "type": "end",
      "actor": "Capitec Bank",
      "description": "Relationship terminated and client data retained per statutory timelines (no active credit).",
      "next": []
    }
  ]
}

{
  "process_name": "Banking Account Onboarding",
  "actors": [
    "Customer",
    "Bank"
  ],
  "steps": [
    {
      "id": "start_01",
      "type": "start",
      "actor": "Customer",
      "description": "Customer decides to open a new bank account.",
      "next": [
        "action_01"
      ]
    },
    {
      "id": "action_01",
      "type": "action",
      "actor": "Customer",
      "description": "Customer completes and submits an account application form (online or in-person).",
      "next": [
        "action_02"
      ]
    },
    {
      "id": "action_02",
      "type": "action",
      "actor": "Bank",
      "description": "Bank reviews the application, performs identity verification (KYC), and necessary background/credit checks.",
      "next": [
        "decision_01"
      ]
    },
    {
      "id": "decision_01",
      "type": "decision",
      "actor": "Bank",
      "description": "Is the account application approved based on internal policies and verification results?",
      "next": [
        "action_03",
        "action_04"
      ]
    },
    {
      "id": "action_03",
      "type": "action",
      "actor": "Bank",
      "description": "Bank informs the customer that their application has been rejected and provides a reason (if permissible).",
      "next": [
        "end_01"
      ]
    },
    {
      "id": "action_04",
      "type": "action",
      "actor": "Bank",
      "description": "Bank opens the new account, assigns an account number, and sets up initial services.",
      "next": [
        "action_05"
      ]
    },
    {
      "id": "action_05",
      "type": "action",
      "actor": "Bank",
      "description": "Bank prepares and dispatches a welcome kit, including debit card, online banking credentials, and relevant account information.",
      "next": [
        "action_06"
      ]
    },
    {
      "id": "action_06",
      "type": "action",
      "actor": "Customer",
      "description": "Customer receives the welcome kit, activates the debit card, and sets up online banking access.",
      "next": [
        "end_02"
      ]
    },
    {
      "id": "end_01",
      "type": "end",
      "actor": "Bank",
      "description": "Process concludes with the account application rejected.",
      "next": []
    },
    {
      "id": "end_02",
      "type": "end",
      "actor": "Customer",
      "description": "Customer is fully onboarded and can utilize their new bank account services.",
      "next": []
    }
  ]
}

{
  "process_name": "Banking Account Onboarding",
  "actors": [
    "Customer",
    "Bank Representative",
    "System"
  ],
  "steps": [
    {
      "id": "start_01",
      "type": "start",
      "actor": "Customer",
      "description": "Customer initiates the account opening process (e.g., in-branch, online).",
      "next": [
        "action_02"
      ]
    },
    {
      "id": "action_02",
      "type": "action",
      "actor": "Customer",
      "description": "Customer provides necessary documentation (ID, proof of address) and personal details.",
      "next": [
        "action_03"
      ]
    },
    {
      "id": "action_03",
      "type": "action",
      "actor": "Bank Representative",
      "description": "Bank Representative reviews provided documents, verifies identity, and enters customer data into the system.",
      "next": [
        "action_04"
      ]
    },
    {
      "id": "action_04",
      "type": "action",
      "actor": "System",
      "description": "System performs automated identity verification, compliance checks (AML, KYC), and credit checks.",
      "next": [
        "decision_05"
      ]
    },
    {
      "id": "decision_05",
      "type": "decision",
      "actor": "System",
      "description": "Are all checks successful and is the application approved?",
      "next": [
        "action_06",
        "action_09"
      ]
    },
    {
      "id": "action_06",
      "type": "action",
      "actor": "Bank Representative",
      "description": "Bank Representative finalizes account setup, assigns account numbers, and prepares welcome documents.",
      "next": [
        "action_07"
      ]
    },
    {
      "id": "action_07",
      "type": "action",
      "actor": "Bank Representative",
      "description": "Bank Representative offers and sets up additional services (e.g., online banking, debit card, mobile app access).",
      "next": [
        "action_08"
      ]
    },
    {
      "id": "action_08",
      "type": "action",
      "actor": "Customer",
      "description": "Customer signs the account agreement and receives the welcome pack with account details.",
      "next": [
        "end_success_10"
      ]
    },
    {
      "id": "end_success_10",
      "type": "end",
      "actor": "Customer",
      "description": "Account Successfully Onboarded.",
      "next": []
    },
    {
      "id": "action_09",
      "type": "action",
      "actor": "Bank Representative",
      "description": "Bank Representative informs the customer of the application rejection, providing reasons if legally permissible.",
      "next": [
        "end_rejection_11"
      ]
    },
    {
      "id": "end_rejection_11",
      "type": "end",
      "actor": "Bank Representative",
      "description": "Application Rejected.",
      "next": []
    }
  ]
}

{
  "process_name": "Credit Application Process",
  "actors": [
    "Applicant",
    "Bank Employee",
    "Credit Analyst",
    "Underwriter",
    "Bank System"
  ],
  "steps": [
    {
      "id": "start_1",
      "type": "start",
      "actor": "Applicant",
      "description": "Applicant decides to apply for credit.",
      "next": [
        "action_1"
      ]
    },
    {
      "id": "action_1",
      "type": "action",
      "actor": "Applicant",
      "description": "Completes and submits credit application form and required supporting documents.",
      "next": [
        "action_2"
      ]
    },
    {
      "id": "action_2",
      "type": "action",
      "actor": "Bank Employee",
      "description": "Receives application, verifies completeness, and inputs data into bank system.",
      "next": [
        "action_3"
      ]
    },
    {
      "id": "action_3",
      "type": "action",
      "actor": "Credit Analyst",
      "description": "Performs credit check, assesses creditworthiness, and generates a preliminary score/report.",
      "next": [
        "decision_1"
      ]
    },
    {
      "id": "decision_1",
      "type": "decision",
      "actor": "Underwriter",
      "description": "Does the applicant's preliminary credit profile meet minimum eligibility criteria?",
      "next": [
        "action_4_yes",
        "action_5_no"
      ]
    },
    {
      "id": "action_4_yes",
      "type": "action",
      "actor": "Underwriter",
      "description": "Conducts detailed financial review, risk assessment, and policy adherence check.",
      "next": [
        "decision_2"
      ]
    },
    {
      "id": "action_5_no",
      "type": "action",
      "actor": "Bank Employee",
      "description": "Notifies applicant of initial rejection based on preliminary assessment.",
      "next": [
        "end_1"
      ]
    },
    {
      "id": "decision_2",
      "type": "decision",
      "actor": "Underwriter",
      "description": "Approve or deny the application based on full review?",
      "next": [
        "action_6_approve",
        "action_7_deny"
      ]
    },
    {
      "id": "action_6_approve",
      "type": "action",
      "actor": "Bank Employee",
      "description": "Prepares and extends a credit offer (e.g., loan terms, credit limit) to the applicant.",
      "next": [
        "decision_3"
      ]
    },
    {
      "id": "action_7_deny",
      "type": "action",
      "actor": "Bank Employee",
      "description": "Notifies applicant of final denial with a reason.",
      "next": [
        "end_2"
      ]
    },
    {
      "id": "decision_3",
      "type": "decision",
      "actor": "Applicant",
      "description": "Does the applicant accept the credit offer?",
      "next": [
        "action_8_yes",
        "action_9_no"
      ]
    },
    {
      "id": "action_8_yes",
      "type": "action",
      "actor": "Applicant",
      "description": "Signs the credit agreement and returns it to the bank.",
      "next": [
        "action_10"
      ]
    },
    {
      "id": "action_9_no",
      "type": "action",
      "actor": "Applicant",
      "description": "Declines the credit offer.",
      "next": [
        "end_3"
      ]
    },
    {
      "id": "action_10",
      "type": "action",
      "actor": "Bank System",
      "description": "Activates the credit line or disburses funds according to the agreement.",
      "next": [
        "end_4"
      ]
    },
    {
      "id": "end_1",
      "type": "end",
      "actor": "N/A",
      "description": "Credit application process ends (Rejected at preliminary stage).",
      "next": []
    },
    {
      "id": "end_2",
      "type": "end",
      "actor": "N/A",
      "description": "Credit application process ends (Denied after full review).",
      "next": []
    },
    {
      "id": "end_3",
      "type": "end",
      "actor": "N/A",
      "description": "Credit application process ends (Offer declined by applicant).",
      "next": []
    },
    {
      "id": "end_4",
      "type": "end",
      "actor": "N/A",
      "description": "Credit application process ends (Credit activated/funds disbursed).",
      "next": []
    }
  ]
}

{
  "process_name": "Fraud Case Handling Process",
  "actors": [
    "Fraud Team",
    "AML",
    "Fraud Forum",
    "Fraud Managers",
    "Account Maintenance Team",
    "Forex Account Maintenance",
    "Credit & Collections & Legal",
    "Merchant Services",
    "Rental Finance",
    "TPMM"
  ],
  "steps": [
    {
      "id": "S1",
      "type": "start",
      "actor": "Fraud Team",
      "description": "Fraud Email Received",
      "next": [
        "A1"
      ]
    },
    {
      "id": "A1",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Create Case in Salesforce",
      "next": [
        "A2"
      ]
    },
    {
      "id": "A2",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Investigate Case & Populate Fraud Terminations Template (includes checking BaNCS, placing STOP50, noting termination dates, documenting funds)",
      "next": [
        "D1"
      ]
    },
    {
      "id": "D1",
      "type": "decision",
      "actor": "Fraud Team",
      "description": "Requires AML Related Party Checks or Statement Monitoring for Further Investigation?",
      "next": [
        "A3",
        "D2"
      ]
    },
    {
      "id": "A3",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Route Case to AML for Investigation",
      "next": [
        "D2"
      ]
    },
    {
      "id": "D2",
      "type": "decision",
      "actor": "Fraud Team",
      "description": "Is Case Complex?",
      "next": [
        "A4",
        "A5"
      ]
    },
    {
      "id": "A4",
      "type": "action",
      "actor": "Fraud Forum",
      "description": "Deliberate Case in Weekly Fraud Forum",
      "next": [
        "A5"
      ]
    },
    {
      "id": "A5",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Obtain Signatures (Two Fraud Managers, One AML)",
      "next": [
        "D3"
      ]
    },
    {
      "id": "D3",
      "type": "decision",
      "actor": "Fraud Team",
      "description": "Fraud Suspect Status?",
      "next": [
        "A6",
        "A7"
      ]
    },
    {
      "id": "A6",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Suspect Cleared: Lift STOP50",
      "next": [
        "D4"
      ]
    },
    {
      "id": "A7",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Suspect Guilty: Place STOP73",
      "next": [
        "D4"
      ]
    },
    {
      "id": "D4",
      "type": "decision",
      "actor": "Fraud Team",
      "description": "Determine Product Type for Termination Notice Routing",
      "next": [
        "A8",
        "A9",
        "A10",
        "A11",
        "A12",
        "A13"
      ]
    },
    {
      "id": "A8",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Route to Account Maintenance Team (Transact Account)",
      "next": [
        "E1"
      ]
    },
    {
      "id": "A9",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Route to Forex Account Maintenance (Forex Account)",
      "next": [
        "E1"
      ]
    },
    {
      "id": "A10",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Route to Credit & Collections & Legal (Credit Account)",
      "next": [
        "E1"
      ]
    },
    {
      "id": "A11",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Route to Merchant Services (Merchant Account)",
      "next": [
        "E1"
      ]
    },
    {
      "id": "A12",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Route to Rental Finance (Rental Finance Account)",
      "next": [
        "E1"
      ]
    },
    {
      "id": "A13",
      "type": "action",
      "actor": "Fraud Team",
      "description": "Route to TPMM (TPMM Account)",
      "next": [
        "E1"
      ]
    },
    {
      "id": "E1",
      "type": "end",
      "actor": "N/A",
      "description": "Fraud Case Process Completed",
      "next": []
    }
  ]
}

{
  "process_name": "Restaurant POS Order & Payment Flow",
  "actors": [
    "Customer",
    "Waitstaff",
    "POS System",
    "Kitchen"
  ],
  "steps": [
    {
      "id": "S1",
      "type": "start",
      "actor": "Customer",
      "description": "Customer is seated and ready to order.",
      "next": [
        "W1"
      ]
    },
    {
      "id": "W1",
      "type": "action",
      "actor": "Waitstaff",
      "description": "Takes Customer's order for Starters, Mains, and optionally Desserts (3 Starters, multiple Mains, 2 Desserts available).",
      "next": [
        "W2"
      ]
    },
    {
      "id": "W2",
      "type": "action",
      "actor": "Waitstaff",
      "description": "Enters all chosen items into the POS System.",
      "next": [
        "D1"
      ]
    },
    {
      "id": "D1",
      "type": "decision",
      "actor": "Waitstaff",
      "description": "Is the order confirmed by Customer and ready to be sent to the Kitchen?",
      "next": [
        "P1",
        "W3"
      ]
    },
    {
      "id": "W3",
      "type": "action",
      "actor": "Waitstaff",
      "description": "Makes necessary modifications to the order in the POS System (e.g., adding/removing items, changing quantities).",
      "next": [
        "D1"
      ]
    },
    {
      "id": "P1",
      "type": "action",
      "actor": "POS System",
      "description": "Sends the confirmed order details to the Kitchen Display System (KDS) or printer.",
      "next": [
        "K1"
      ]
    },
    {
      "id": "K1",
      "type": "action",
      "actor": "Kitchen",
      "description": "Prepares the food items according to the order.",
      "next": [
        "W4"
      ]
    },
    {
      "id": "W4",
      "type": "action",
      "actor": "Waitstaff",
      "description": "Serves prepared courses to the Customer as they are ready.",
      "next": [
        "W5"
      ]
    },
    {
      "id": "W5",
      "type": "action",
      "actor": "Waitstaff",
      "description": "Generates the bill from the POS System upon Customer's request.",
      "next": [
        "W6"
      ]
    },
    {
      "id": "W6",
      "type": "action",
      "actor": "Waitstaff",
      "description": "Presents the bill to the Customer.",
      "next": [
        "C1"
      ]
    },
    {
      "id": "C1",
      "type": "action",
      "actor": "Customer",
      "description": "Provides payment for the bill.",
      "next": [
        "W7"
      ]
    },
    {
      "id": "W7",
      "type": "action",
      "actor": "Waitstaff",
      "description": "Processes payment through the POS System (e.g., cash, card).",
      "next": [
        "P2"
      ]
    },
    {
      "id": "P2",
      "type": "action",
      "actor": "POS System",
      "description": "Records the transaction, updates inventory, and closes the order.",
      "next": [
        "E1"
      ]
    },
    {
      "id": "E1",
      "type": "end",
      "actor": "Customer",
      "description": "Customer leaves the restaurant.",
      "next": []
    }
  ]
}
